import React,{useState, useEffect} from 'react'

const API = process.env.REACT_APP_API;

export const Users = () => {
    const[name, setName] = useState("");
    const[email, setEmail] = useState("");
    const[password, setPassword] = useState("");
    
    let [users, setUsers] = useState([]);

    const handleSubmit = async (e) =>{
        e.preventDefault();
        const res = await fetch(`${API}/users`, {
            method: "POST",
            headers:{
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                name,
                email,
                password
            }),
        });
        const data = await res.json();
        console.log(data)
    }

    const getUsers = async () =>{
       const res =  await fetch(`${API}/users`)
       const data = await res.json();
       setUsers(data);
       console.log(data)
    }
    useEffect(()=>{
        getUsers();
    }, [])
    const editUser = (id) =>{
        console.log(id)
    }

    const deleteUser = (id) =>{
        //console.log(id)
        fetch()
    }

    return (
        <div className="row">
            <di className="col-md-4">
                <form onSubmit={handleSubmit} className="card card-body">
                    <div className="form-group">
                        <input type="text" 
                            onChange={e => setName(e.target.value)} 
                            value={name} 
                            className="form-control"
                            placeholder="Name"
                            autoFocus
                        />
                    </div>
                    <div className="form-group">
                        <input type="email" 
                            onChange={e => setEmail(e.target.value)} 
                            value={email} 
                            className="form-control"
                            placeholder="Email"
                        />
                    </div>
                    <div className="form-group">
                        <input type="password" 
                            onChange={e => setPassword(e.target.value)} 
                            value={password} 
                            className="form-control"
                            placeholder="Password"
                        />
                    </div>
                    <button className="btn btn-primary btn-block">
                        Create
                    </button>
                </form>
            </di>
            <div className="col-md-6">
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Password</th>
                        <th>Operations</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) =>(
                        <tr key={user._id}>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.password}</td>
                            <td>
                                <button className="btn btn-secondary btn-sm btn-block" onClick={(e) => editUser(user._id)}>Edit</button>
                                <button className="btn btn-danger btn-sm btn-block" onClick={(e) => deleteUser(user._id)}>Delete</button>  
                            </td>                           
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    </div>
    )
}
